mapper.select:
SELECT *
FROM ${tableName}
WHERE stock_symbol = '${stockSymbol}'
	AND event_day = ${eventDay}
	AND call_put = '${callPut}'
	AND expiration = ${expiration}
	AND strike = ${strike}

SELECT *
FROM ${tableName}
WHERE stock_symbol = '${stockSymbol}'
	AND event_day BETWEEN ${startEventDay} AND ${endEventDay} 
	AND call_put = '${callPut}'
	AND expiration = ${expiration}
	AND strike = ${strike}
ORDER BY event_day ASC
